mapper.select:
SELECT function_code, COUNT(DISTINCT user_id) AS count
FROM user_access_log
WHERE function_code LIKE 'REPORT_%'

    AND DATE_FORMAT(access_time,'%Y-%m') = #{date}


    AND DATE(access_time) = #{date}

GROUP BY function_code

SELECT function_code, COUNT(*) AS count
FROM user_access_log
WHERE function_code LIKE 'REPORT_%'

    AND DATE_FORMAT(access_time,'%Y-%m') = #{date}


    AND DATE(access_time) = #{date}

GROUP BY function_code

SELECT COUNT(DISTINCT user_id)
FROM user_access_log
WHERE function_code='LOGIN_TRADEACCOUNT'
AND access_status=1

    AND DATE_FORMAT(access_time,'%Y-%m') = #{date}


    AND DATE(access_time) = #{date}
